	    var StuNme, StuClassName, Sex, DepartmentName, MajorName, ICNumber;         //define
	    var StuInfo;
	    function onDeviceReady() {
	        alert("Device is ready");
	    }

	    function Init() {
	        document.addEventListener('deviceready', onDeviceReady, false);
	    }
	    //TEST CODING
        //Output function
	    function printInfo(StuNme, StuClassName, Sex, DepartmentName, MajorName, ICNumber) {
	        
	      //  alert(StuNme);
            document.getElementById("SName").innerHTML = StuNme;
	      //  alert(StuClassName);
	        document.getElementById("StuClassName").innerHTML = StuClassName;
	      //  alert(Sex);
	        document.getElementById("Sex").innerHTML = Sex;
	     //   alert(DepartmentName);
	        document.getElementById("DepartmentName").innerHTML = DepartmentName;
	     //   alert(MajorName);
	        document.getElementById("MajorName").innerHTML = MajorName;
	        document.getElementById("ICNumber").innerHTML = ICNumber;

	    }
        
        //get data
	    function loadStuInfo() {
	        
	      //  var md5_stuPasswd = hex_md5(stuPasswd);
	        var curStuNum = "123456";
	        var md5_stuPasswd = "e10adc3949ba59abbe56e057f20f883e";
            queryString = "UserName=" + curStuNum + "&PassWord=" + md5_stuPasswd;   
                        
	      
	        var url = "http://222.18.158.220:9001/Authentication.svc/GetStudentInfo?" + queryString + "&callback=?";
	      // alert("取数据！");
	        if (navigator.onLine) {
	            $.getJSON(url, function (data) {
	                var InfoJSON = $.parseJSON(data);
	                if (InfoJSON["Success"] == "true") {
	                    printInfo(InfoJSON["StuInfo"][0]["StuNme"], InfoJSON["StuInfo"][0]["StuClassName"], InfoJSON["StuInfo"][0]["Sex"], InfoJSON["StuInfo"][0]["DepartmentName"], InfoJSON["StuInfo"][0]["MajorName"], InfoJSON["StuInfo"][0]["ICNumber"]);
	                   //     alert("Success");
	                }
	                else {
	                    alert("好像没有你的信息 :(");
	                }
	            });
	        }
	        else {
	            alert("网络好像不给力啊！");
	        }
	    }

       

	    //OVER
	    //
	    loadStuInfo();
	</script>
	
</head>
<body onload="Init()">
    <!-- Header -->
    <div class="header_wrapper">
