                    <td class="td_info" id ="DepartmentName"></td>
                </tr>
                <tr>
                    <td class="td_foot">专业：</td>
                    <td class="td_info" id="MajorName"></td>
                </tr>
                
            </table>
        </div>
        </div>
    </div>
   

    <!-- Footer -->
    <div class="footer_wrapper">
        <div class="footer_wrapper_fixed">
       
        </div>
    </div>
</body>
